select ut.user_id, ut.username, ut.password, ut.email, ut.phoneNumber, ut.account_expired, ut.account_locked, ut.credentials_expired, ut.account_enabled, ut.created_at,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb ut left outer join user_role_tb urt on(urt.user_id = ut.user_id) left outer join role_tb rt on(rt.role_id = urt.role_id) where ut.user_id = #{userId}

